mapper.insert:

insert into PUBLIC.ACTIVITIES_GROUP (DESCRIPTION, NAME, TEACHER_ID
  )
values (#{description,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{teacherId,jdbcType=INTEGER}
  )

mapper.update:

update PUBLIC.ACTIVITIES_GROUP
set DESCRIPTION = #{description,jdbcType=VARCHAR},
  NAME = #{name,jdbcType=VARCHAR},
  TEACHER_ID = #{teacherId,jdbcType=INTEGER}
where ID = #{id,jdbcType=INTEGER}

mapper.select:

select ID, DESCRIPTION, NAME, TEACHER_ID
from PUBLIC.ACTIVITIES_GROUP
where ID = #{id,jdbcType=INTEGER}

SELECT * FROM PUBLIC.ACTIVITIES_GROUP WHERE ACTIVITIES_GROUP.ID NOT IN (
  SELECT actgr.id FROM PUBLIC.ACTIVITIES_GROUP actgr
  INNER JOIN PUBLIC.CHILDREN_ACTIVITIES ca ON actgr.ID = ca.ACTIVITIES_GROUPS_ID
  INNER JOIN PUBLIC.CHILDREN child ON ca.CHILDRENLIST_ID = child.ID
  WHERE child.id = #{id,jdbcType=INTEGER}
);
